<script>
function getCookie(name){
    const strcookie = document.cookie;//获取cookie字符串
    const arrcookie = strcookie.split("; ");//分割
    //遍历匹配
    for (let i = 0; i < arrcookie.length; i++) {
        const arr = arrcookie[i].split("=");
        if (arr[0] === name){
            return unescape(decodeURI(arr[1]));
        }
    }
    return "";
}
function getCommentInfo(){
    let nick = document.getElementById('author');
    let mail = document.getElementById('mail');
    let url = document.getElementById('url');
    if (nick) nick.value = getCookie('<?php echo Typecho_Cookie::getPrefix()?>__typecho_remember_author');
    if (mail) mail.value = getCookie('<?php echo Typecho_Cookie::getPrefix()?>__typecho_remember_mail');
    if (url) url.value = getCookie('<?php echo Typecho_Cookie::getPrefix()?>__typecho_remember_url');
    $.post('<?php getIsLogin(); ?>',{action:'find'},function (res) {
        res = JSON.parse(res);
        switch(res.code){
            case 0 :
        $('#loginshowlabel').hide();
    $('#nologinshow').show();
    break;
    case 1 :
        $('#loginshowlabel').show();
    $('#nologinshow').hide();
    $('#authorname').html(res.name);
    break;
        }
    })
}
getCommentInfo();
</script>
